import React, { useState } from "react";
import { motion } from "framer-motion";
import tw from "twin.macro";
import styled, { css } from "styled-components/macro"; //eslint-disable-line
import { Container, ContentWithPaddingXl } from "components/misc/Layouts.js";
import { SectionHeading, Subheading as SubheadingBase } from "components/misc/Headings.js";
import { SectionDescription } from "components/misc/Typography.js";
import { ReactComponent as ChevronDownIcon } from "feather-icons/dist/icons/chevron-down.svg";

const PrimaryBackgroundContainer = tw(Container)`-mx-8 px-8 bg-blue-800 text-gray-100`;

const HeadingContainer = tw.div``;
const Subheading = tw(SubheadingBase)`text-center text-gray-100 mb-4`;
const Heading = tw(SectionHeading)``;
const Description = tw(SectionDescription)`mx-auto text-center text-gray-300`;

const FaqsContainer = tw.div`mt-10 sm:mt-16 w-full flex-1 lg:flex justify-between items-start max-w-screen-lg mx-auto`;
const FaqsColumn = tw.div`w-full lg:max-w-lg lg:mr-12 last:mr-0`;
const Faq = tw.div`select-none cursor-pointer border-b-2 border-blue-500 hover:border-blue-700 transition-colors duration-300 py-6`;
const Question = tw.div`flex justify-between items-center`;
const QuestionText = tw.div`text-sm sm:text-lg font-semibold tracking-wide`;
const QuestionToggleIcon = styled(motion.span)`
  ${tw`ml-2 transition duration-300`}
  svg {
    ${tw`w-6 h-6`}
  }
`;
const Answer = tw(motion.div)`hidden text-sm font-normal mt-4 text-gray-300`;

export default ({
  subheading = "",
  heading = "Plan de estudios",
  faqs = [
    {
      question: "Primer Cuatrimestre",
      answer:
      (<>• Cálculo diferencial<br/>• Inglés l
      <br/>• Introducción a la ingeniería en energía
      <br/>• Mecánica con laboratorio<br/>• Programación
      <br/>• Transformaciones químicas con laboratorio
      <br/>• Valores del ser<br/></>),
    },
    {
      question: "Segundo Cuatrimestre",
      answer:
      (<>• Cálculo Cálculo integral
      <br/>• Inglés lI
      <br/>• InteligencionaI emocional
      <br/>• Laboratorio de simulación y diseño por computadora
      <br/>• Óptica
      <br/>• Seminario de ingeniería en energía térmica
      <br/>• Termodinámica con laboratorio<br/></>)
    },
    {
      question: "Tercer Cuatrimestre",
      answer:
      (<>• Álgebra lineal
      <br/>• Cálculo de varias variables
      <br/>• Desarrollo interpersonal
      <br/>• Inglés III
      <br/>• Mecánica de fluidos con laboratorio
      <br/>• Seminario de ingeniería en energía eólica
      <br/>• Transferencia de calor y masa<br/></>)
    },
    {
      question: "Cuarto Cuatrimestre",
      answer:(<>• Ecuaciones diferenciales
      <br/>• Energía del hidrógeno con laboratorio
      <br/>• Estancia I
      <br/>• Física moderna
      <br/>• Habilidades del pensamiento
      <br/>• Inglés IV
      <br/>• Seminario de ingeniería en energía del hidrógeno<br/></>)

    },
    {
      question: "Quinto Cuatrimestre",
      answer:(<>• Ecuaciones diferenciales parciales
      <br/>• Electricidad y magnetismo con laboratorio
      <br/>• Estado sólido
      <br/>• Habilidades organizacionales
      <br/>• Inglés V
      <br/>• Seminario de ingeniería en energía fotovoltaica
      <br/>• Sistemas fotovoltaicos con laboratorio<br/></>)

    },
    {
      question: "Sexto Cuatrimestre",
      answer:(<>• Biomasa con laboratorio
      <br/>• Celdas de combustible<br/>• Electroquímica
      <br/>• Ética profesional<br/>• Inglés VI
      <br/>• Máquinas eléctricas
      <br/>• Seminario de ingeniería en energía de biomasa<br/></>)

    },
    {
      question: "Séptimo Cuatrimestre",
      answer:(<>• Contabilidad empresarial
      <br/>• Dimensionamiento de instalaciones eléctricas
      <br/>• Estancia II
      <br/>• Física nuclear con laboratorio
      <br/>• Ingeniería ambiental
      <br/>• Inglés VII
      <br/>• Metrología e instrumentación<br/></>)

    },
    {
      question: "Octavo Cuatrimestre",
      answer:(<>• Ahorro y uso eficiente de energía
      <br/>• Almacenamiento de energía solar
      <br/>• Diseño de sistemas fotovoltaicos
      <br/>• Energía hidráulica con laboratorio
      <br/>• Ingeniería energética
      <br/>• Inglés VlII
      <br/>• Seguridad industrial<br/></>)

    },
    {
      question: "Noveno Cuatrimestre",
      answer:(<>• Diseño de sistemas fototérmicos
      <br/>• Ética en los negocios
      <br/>• Gestión de proyectos
      <br/>• Inglés IX
      <br/>• Innovación tecnológica
      <br/>• Introducción a la administración
      <br/>• Introducción a la arquitectura bioclimática<br/></>)

    },
    {
      question: "Décimo Cuatrimestre",
      answer: "• Estadía"

    },
  ]
}) => {
  const faqCol1 = [];
  const faqCol2 = [];
  const [activeQuestionIndex, setActiveQuestionIndex] = useState(null);

  const toggleQuestion = questionIndex => {
    if (activeQuestionIndex === questionIndex) setActiveQuestionIndex(null);
    else setActiveQuestionIndex(questionIndex);
  };

  faqs.map((faq, index) => {
    const renderedFaq = (
      <Faq key={index} onClick={() => toggleQuestion(index)}>
        <Question>
          <QuestionText>{faq.question}</QuestionText>
          <QuestionToggleIcon
            variants={{
              collapsed: { rotate: 0 },
              open: { rotate: -180 }
            }}
            initial="collapsed"
            animate={activeQuestionIndex === index ? "open" : "collapsed"}
            transition={{ duration: 0.02, ease: [0.04, 0.62, 0.23, 0.98] }}
          >
            <ChevronDownIcon />
          </QuestionToggleIcon>
        </Question>
        <Answer
          variants={{
            open: { opacity: 1, height: "auto", marginTop: "16px", display: "block" },
            collapsed: { opacity: 0, height: 0, marginTop: "0px", display: "none" }
          }}
          initial="collapsed"
          animate={activeQuestionIndex === index ? "open" : "collapsed"}
          transition={{ duration: 0.3, ease: [0.04, 0.62, 0.23, 0.98] }}
        >
          {faq.answer}
        </Answer>
      </Faq>
    );

    if (index % 2 === 0) faqCol1.push(renderedFaq);
    else faqCol2.push(renderedFaq);

    return null;
  });
  return (
    <PrimaryBackgroundContainer id='Plan'>
      <ContentWithPaddingXl>
        <HeadingContainer>
          {subheading && <Subheading>{subheading}</Subheading>}
          <Heading>{heading}</Heading>

        </HeadingContainer>
        <FaqsContainer>
          <FaqsColumn>{faqCol1}</FaqsColumn>
          <FaqsColumn>{faqCol2}</FaqsColumn>
        </FaqsContainer>
      </ContentWithPaddingXl>
    </PrimaryBackgroundContainer>
  );
};
